package conditionalLoops;

public class Right_Triangle_Pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//i for rows and j for columns   
		int i, j, row=6;   
		//outer loop for rows  
		for(i=0; i<row; i++)   
		{   
		
		for(j=0; j<=i; j++)   
		{   
		   
		System.out.print("* ");   
		}   
		  
		System.out.println();   
		}   
		}   
	

	}


